import React,{useState} from 'react';
import './Cards.css';

//import Card from './Card.jsx';

export default function Cards({cities,check}) {
  //if(cities){
    var infoCities=cities
  if(check){  
    return (
      <div className='cards'>
        {infoCities.cities.map(el => 
            <div className="card">
              <div id="closeIcon" className="row">
                  <button onClick={function click(){
                    infoCities=infoCities.cities.filter(c=>c.id!==el.id);
                    <Cards cities={infoCities} />
                    console.log(infoCities)
                  }} className="btn btn-sm btn-danger">X</button>
              </div>
              <div className="card-body">
                <h5 className="card-title">{el.name}</h5>
                <div className="row">
                  <div className="col-sm-4 col-md-4 col-lg-4">
                    <p>Min</p>
                    <p>{el.min}°</p>
                  </div>
                  <div className="col-sm-4 col-md-4 col-lg-4">
                    <p>Max</p>
                    <p>{el.max}°</p>
                  </div>
                  <div className="col-sm-4 col-md-4 col-lg-4">
                    <img className="iconoClima" src={"http://openweathermap.org/img/wn/"+el.img+"@2x.png"} width="80" height="80" alt="" />
                  </div>
                </div>
              </div>
            </div>
         )}
      </div>
    );
  } else {
    return(
      <div>Sin ciudades</div>
    )
  }
}
