import React,{useState} from 'react';
import Logo from '../logoHenry.png'
import './Nav.css';

export default function Nav({onSearch}) {

  const [city,setCities] = useState("") 

  function handleInputChange(e){
    e.preventDefault() 
    setCities(e.target.value) 
  }

  function handleSubmit(e){
    e.preventDefault() 
    alert(city)
}

  return (
    <nav class="navbar navbar-light bg-light">
      <form class="form-inline">
        <input class="form-control mr-sm-2" type="text" placeholder="Ciudad..." aria-label="Search" onChange = {(e) => handleInputChange(e)}/>
        <button class="btn btn-outline-success my-2 my-sm-0" type="submit"  onClick={e => handleSubmit(e)}>Agregar</button>
      </form>
  </nav>
  );
};
