import './Cards.css';

import Card from './Card.jsx';

export default function Cards({cities}) {
  var citiesInfo=cities.cities;
  function close(){
    citiesInfo=citiesInfo.filter(el=>el.id!==id)
  }
  if(cities){
    return (
      <div className='cards'>
        {citiesInfo.map(el => <Card
            max={el.max}
            min={el.min}
            name={el.name}
            img={el.img}
            onClose={close}
            id={el.id}
          /> )}
      </div>
    );
  } else {
    return(
      <div>Sin ciudades</div>
    )
  }
}
